package fsm.validation;

import fsm.FSM;
import fsm.Transition;

import org.eclipse.emf.common.util.EList;

/**
 * A sample validator interface for {@link fsm.State}.
 * This doesn't really do anything, and it's not a real EMF artifact.
 * It was generated by the org.eclipse.emf.examples.generator.validator plug-in to illustrate how EMF's code generator can be extended.
 * This can be disabled with -vmargs -Dorg.eclipse.emf.examples.generator.validator=false.
 */
public interface StateValidator
{
  boolean validate();

  boolean validateName(String value);
  boolean validateOwningFSM(FSM value);
  boolean validateIncomingTransition(EList<Transition> value);
  boolean validateOutgoingTransition(EList<Transition> value);
}
