package org.smartadapters.core.resource.core.mopp;

// A CodeCompletionHelper can be used to derive completion proposals for partial
// documents. It runs the parser generated by EMFText in a special mode (i.e., the
// rememberExpectedElements mode). Based on the elements that are expected by the
// parser for different regions in the document, valid proposals are computed.
public class CoreCodeCompletionHelper {
	
	// Computes a set of proposals for the given document assuming the cursor is
	// at 'cursorOffset'. The proposals are derived using the meta information, i.e.,
	// the generated language plug-in.
	//
	// @param metaInformation
	// @param content the documents content
	// @param cursorOffset
	// @return
	public org.smartadapters.core.resource.core.mopp.CoreCompletionProposal[] computeCompletionProposals(org.smartadapters.core.resource.core.ICoreTextResource originalResource, String content, int cursorOffset) {
		org.eclipse.emf.ecore.resource.ResourceSet resourceSet = new org.eclipse.emf.ecore.resource.impl.ResourceSetImpl();
		// the shadow resource needs the same URI because reference resolvers may use the URI to resolve external references
		org.smartadapters.core.resource.core.ICoreTextResource resource = (org.smartadapters.core.resource.core.ICoreTextResource) resourceSet.createResource(originalResource.getURI());
		java.io.ByteArrayInputStream inputStream = new java.io.ByteArrayInputStream(content.getBytes());
		org.smartadapters.core.resource.core.mopp.CoreMetaInformation metaInformation = resource.getMetaInformation();
		org.smartadapters.core.resource.core.ICoreTextParser parser = metaInformation.createParser(inputStream, null);
		org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[] expectedElements = parseToExpectedElements(parser, resource);
		if (expectedElements == null) {
			return new org.smartadapters.core.resource.core.mopp.CoreCompletionProposal[0];
		}
		if (expectedElements.length == 0) {
			return new org.smartadapters.core.resource.core.mopp.CoreCompletionProposal[0];
		}
		java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedAfterCursor = java.util.Arrays.asList(getElementsExpectedAt(expectedElements, cursorOffset));
		java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedBeforeCursor = java.util.Arrays.asList(getElementsExpectedAt(expectedElements, cursorOffset - 1));
		setPrefixes(expectedAfterCursor, content, cursorOffset);
		setPrefixes(expectedBeforeCursor, content, cursorOffset);
		// first we derive all possible proposals from the set of elements that are expected at the cursor position
		java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> allProposals = new java.util.LinkedHashSet<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal>();
		java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> rightProposals = deriveProposals(expectedAfterCursor, content, resource, cursorOffset);
		java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> leftProposals = deriveProposals(expectedBeforeCursor, content, resource, cursorOffset);
		// second, the set of left proposals (i.e., the ones before the cursor) is
		// checked for emptiness. if the set is empty, the right proposals (i.e., 
		// the ones after the cursor are removed, because it does not make sense to
		// propose them until the element before the cursor was completed
		allProposals.addAll(leftProposals);
		if (leftProposals.isEmpty()) {
			allProposals.addAll(rightProposals);
		}
		// third, the proposals are sorted according to their relevance
		// proposals that matched the prefix are preferred over ones that did not
		// afterward proposals are sorted alphabetically
		final java.util.List<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> sortedProposals = new java.util.ArrayList<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal>(allProposals);
		java.util.Collections.sort(sortedProposals);
		return sortedProposals.toArray(new org.smartadapters.core.resource.core.mopp.CoreCompletionProposal[sortedProposals.size()]);
	}
	
	public org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[] parseToExpectedElements(org.smartadapters.core.resource.core.ICoreTextParser parser, org.smartadapters.core.resource.core.ICoreTextResource resource) {
		final java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedElements = parser.parseToExpectedElements(null, resource);
		if (expectedElements == null) {
			return new org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[0];
		}
		removeDuplicateEntries(expectedElements);
		removeInvalidEntriesAtEnd(expectedElements);
		return expectedElements.toArray(new org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[expectedElements.size()]);
	}
	
	private void removeDuplicateEntries(java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedElements) {
		for (int i = 0; i < expectedElements.size() - 1; i++) {
			org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal elementAtIndex = expectedElements.get(i);
			for (int j = i + 1; j < expectedElements.size();) {
				org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal elementAtNext = expectedElements.get(j);
				if (elementAtIndex.equals(elementAtNext) && elementAtIndex.getStartExcludingHiddenTokens() == elementAtNext.getStartExcludingHiddenTokens()) {
					expectedElements.remove(j);
				} else {
					j++;
				}
			}
		}
	}
	
	private void removeInvalidEntriesAtEnd(java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedElements) {
		for (int i = 0; i < expectedElements.size() - 1;) {
			org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal elementAtIndex = expectedElements.get(i);
			org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal elementAtNext = expectedElements.get(i + 1);
			if (elementAtIndex.getStartExcludingHiddenTokens() == elementAtNext.getStartExcludingHiddenTokens() && shouldRemove(elementAtIndex.getFollowSetID(), elementAtNext.getFollowSetID())) {
				expectedElements.remove(i + 1);
			} else {
				i++;
			}
		}
	}
	
	public boolean shouldRemove(int followSetID1, int followSetID2) {
		return followSetID1 != followSetID2;
	}
	
	private String findPrefix(java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedElements, org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal expectedAtCursor, String content, int cursorOffset) {
		if (cursorOffset < 0) {
			return "";
		}
		int end = 0;
		for (org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal expectedElement : expectedElements) {
			if (expectedElement == expectedAtCursor) {
				final int start = expectedElement.getStartExcludingHiddenTokens();
				if (start >= 0  && start < Integer.MAX_VALUE) {
					end = start;
				}
				break;
			}
		}
		end = Math.min(end, cursorOffset);
		final String prefix = content.substring(end, Math.min(content.length(), cursorOffset));
		return prefix;
	}
	
	private java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> deriveProposals(java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedElements, String content, org.smartadapters.core.resource.core.ICoreTextResource resource, int cursorOffset) {
		java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> resultSet = new java.util.HashSet<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal>();
		for (org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal expectedElement : expectedElements) {
			resultSet.addAll(deriveProposals(expectedElement, content, resource, cursorOffset));
		}
		return resultSet;
	}
	
	private java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> deriveProposals(org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal expectedTerminal, String content, org.smartadapters.core.resource.core.ICoreTextResource resource, int cursorOffset) {
		org.smartadapters.core.resource.core.mopp.CoreMetaInformation metaInformation = resource.getMetaInformation();
		org.smartadapters.core.resource.core.ICoreLocationMap locationMap = resource.getLocationMap();
		org.smartadapters.core.resource.core.ICoreExpectedElement expectedElement = (org.smartadapters.core.resource.core.ICoreExpectedElement) expectedTerminal.getTerminal();
		if (expectedElement instanceof org.smartadapters.core.resource.core.mopp.CoreExpectedCsString) {
			org.smartadapters.core.resource.core.mopp.CoreExpectedCsString csString = (org.smartadapters.core.resource.core.mopp.CoreExpectedCsString) expectedElement;
			return deriveProposal(csString, content, expectedTerminal.getPrefix(), cursorOffset);
		} else if (expectedElement instanceof org.smartadapters.core.resource.core.mopp.CoreExpectedStructuralFeature) {
			org.smartadapters.core.resource.core.mopp.CoreExpectedStructuralFeature expectedFeature = (org.smartadapters.core.resource.core.mopp.CoreExpectedStructuralFeature) expectedElement;
			org.eclipse.emf.ecore.EStructuralFeature feature = expectedFeature.getFeature();
			org.eclipse.emf.ecore.EClassifier featureType = feature.getEType();
			java.util.List<org.eclipse.emf.ecore.EObject> elementsAtCursor = locationMap.getElementsAt(cursorOffset);
			org.eclipse.emf.ecore.EObject container = null;
			// we need to skip the proxy elements at the cursor, because they are not the container for the reference we try to complete
			for (int i = 0; i < elementsAtCursor.size(); i++) {
				container = elementsAtCursor.get(i);
				if (!container.eIsProxy()) {
					break;
				}
			}
			if (feature instanceof org.eclipse.emf.ecore.EReference) {
				org.eclipse.emf.ecore.EReference reference = (org.eclipse.emf.ecore.EReference) feature;
				if (featureType instanceof org.eclipse.emf.ecore.EClass) {
					if (reference.isContainment()) {
						// the FOLLOW set should contain only non-containment references
						assert false;
					} else {
						return handleNCReference(metaInformation, container, reference, expectedTerminal.getPrefix());
					}
				}
			} else if (feature instanceof org.eclipse.emf.ecore.EAttribute) {
				org.eclipse.emf.ecore.EAttribute attribute = (org.eclipse.emf.ecore.EAttribute) feature;
				if (featureType instanceof org.eclipse.emf.ecore.EEnum) {
					org.eclipse.emf.ecore.EEnum enumType = (org.eclipse.emf.ecore.EEnum) featureType;
					return deriveProposals(expectedTerminal, enumType, content, cursorOffset);
				} else {
					// handle EAttributes (derive default value depending on
					// the type of the attribute, figure out token resolver, and
					// call deResolve())
					return handleAttribute(metaInformation, expectedFeature, container, attribute, expectedTerminal.getPrefix());
				}
			} else {
				// there should be no other subclass of EStructuralFeature
				assert false;
			}
		} else {
			// there should be no other class implementing IExpectedElement
			assert false;
		}
		return java.util.Collections.emptyList();
	}
	
	private java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> deriveProposals(org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal expectedElement, org.eclipse.emf.ecore.EEnum enumType, String content, int cursorOffset) {
		java.util.Collection<org.eclipse.emf.ecore.EEnumLiteral> enumLiterals = enumType.getELiterals();
		java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> result = new java.util.HashSet<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal>();
		for (org.eclipse.emf.ecore.EEnumLiteral literal : enumLiterals) {
			String proposal = literal.getLiteral();
			String prefix = expectedElement.getPrefix();
			if (matches(proposal, prefix)) {
				result.add(new org.smartadapters.core.resource.core.mopp.CoreCompletionProposal(proposal, prefix, !"".equals(prefix), true));
			}
		}
		return result;
	}
	
	private java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> handleNCReference(org.smartadapters.core.resource.core.mopp.CoreMetaInformation metaInformation, org.eclipse.emf.ecore.EObject container, org.eclipse.emf.ecore.EReference reference, java.lang.String prefix) {
		// proposals for non-containment references are derived by calling the
		// reference resolver switch in fuzzy mode.
		org.smartadapters.core.resource.core.ICoreReferenceResolverSwitch resolverSwitch = metaInformation.getReferenceResolverSwitch();
		org.smartadapters.core.resource.core.ICoreReferenceResolveResult<org.eclipse.emf.ecore.EObject> result = new org.smartadapters.core.resource.core.mopp.CoreReferenceResolveResult<org.eclipse.emf.ecore.EObject>(true);
		resolverSwitch.resolveFuzzy(prefix, container, reference, 0, result);
		java.util.Collection<org.smartadapters.core.resource.core.ICoreReferenceMapping<org.eclipse.emf.ecore.EObject>> mappings = result.getMappings();
		if (mappings != null) {
			java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> resultSet = new java.util.HashSet<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal>();
			for (org.smartadapters.core.resource.core.ICoreReferenceMapping<org.eclipse.emf.ecore.EObject> mapping : mappings) {
				final String identifier = mapping.getIdentifier();
				// the proposal can be added without checking the prefix because this is
				// performed by the reference resolvers
				resultSet.add(new org.smartadapters.core.resource.core.mopp.CoreCompletionProposal(identifier, prefix, true, true));
			}
			return resultSet;
		}
		return java.util.Collections.emptyList();
	}
	
	private java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> handleAttribute(org.smartadapters.core.resource.core.mopp.CoreMetaInformation metaInformation, org.smartadapters.core.resource.core.mopp.CoreExpectedStructuralFeature expectedFeature, org.eclipse.emf.ecore.EObject container, org.eclipse.emf.ecore.EAttribute attribute, java.lang.String prefix) {
		java.lang.Object defaultValue = getDefaultValue(attribute);
		if (defaultValue != null) {
			org.smartadapters.core.resource.core.ICoreTokenResolverFactory tokenResolverFactory = metaInformation.getTokenResolverFactory();
			String tokenName = expectedFeature.getTokenName();
			if (tokenName != null) {
				org.smartadapters.core.resource.core.ICoreTokenResolver tokenResolver = tokenResolverFactory.createTokenResolver(tokenName);
				if (tokenResolver != null) {
					String defaultValueAsString = tokenResolver.deResolve(defaultValue, attribute, container);
					java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> resultSet = new java.util.HashSet<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal>();
					if (matches(defaultValueAsString, prefix)) {
						resultSet.add(new org.smartadapters.core.resource.core.mopp.CoreCompletionProposal(defaultValueAsString, prefix, !"".equals(prefix), true));
					}
					return resultSet;
				}
			}
		}
		return java.util.Collections.emptyList();
	}
	
	private java.lang.Object getDefaultValue(org.eclipse.emf.ecore.EAttribute attribute) {
		String typeName = attribute.getEType().getName();
		if ("EString".equals(typeName)) {
			return "some" + org.smartadapters.core.resource.core.util.CoreStringUtil.capitalize(attribute.getName());
		}
		return attribute.getDefaultValue();
	}
	
	private java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> deriveProposal(org.smartadapters.core.resource.core.mopp.CoreExpectedCsString csString, String content, String prefix, int cursorOffset) {
		String proposal = csString.getValue();
		java.util.Collection<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal> result = new java.util.HashSet<org.smartadapters.core.resource.core.mopp.CoreCompletionProposal>();
		if (matches(proposal, prefix)) {
			result.add(new org.smartadapters.core.resource.core.mopp.CoreCompletionProposal(proposal, prefix, !"".equals(prefix), false));
		}
		return result;
	}
	
	// for each given expected elements the prefix is calculated
	// the prefix depends on the current document content, the cursor position, and
	// the position where the element is expected
	private void setPrefixes(java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedElements, String content, int cursorOffset) {
		if (cursorOffset < 0) {
			return;
		}
		for (org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal expectedElement : expectedElements) {
			java.lang.String prefix = findPrefix(expectedElements, expectedElement, content, cursorOffset);
			expectedElement.setPrefix(prefix);
		}
	}
	
	public org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[] getElementsExpectedAt(org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[] allExpectedElements, int cursorOffset) {
		java.util.List<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal> expectedAtCursor = new java.util.ArrayList<org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal>();
		for (int i = 0; i < allExpectedElements.length; i++) {
			org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal expectedElement = allExpectedElements[i];
			int startIncludingHidden = expectedElement.getStartIncludingHiddenTokens();
			int end = getEnd(allExpectedElements, i);
			if (cursorOffset >= startIncludingHidden && cursorOffset <= end) {
				expectedAtCursor.add(expectedElement);
			}
		}
		return expectedAtCursor.toArray(new org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[expectedAtCursor.size()]);
	}
	
	private int getEnd(org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal[] allExpectedElements, int indexInList) {
		org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal elementAtIndex = allExpectedElements[indexInList];
		int startIncludingHidden = elementAtIndex.getStartIncludingHiddenTokens();
		int startExcludingHidden = elementAtIndex.getStartExcludingHiddenTokens();
		for (int i = indexInList + 1; i < allExpectedElements.length; i++) {
			org.smartadapters.core.resource.core.mopp.CoreExpectedTerminal elementAtI = allExpectedElements[i];
			int startIncludingHiddenForI = elementAtI.getStartIncludingHiddenTokens();
			int startExcludingHiddenForI = elementAtI.getStartExcludingHiddenTokens();
			if (startIncludingHidden != startIncludingHiddenForI || startExcludingHidden != startExcludingHiddenForI) {
				return startIncludingHiddenForI - 1;
			}
		}
		return Integer.MAX_VALUE;
	}
	
	private boolean matches(java.lang.String proposal, java.lang.String prefix) {
		return (proposal.startsWith(prefix) || org.smartadapters.core.resource.core.util.CoreStringUtil.matchCamelCase(prefix, proposal) != null) && !proposal.equals(prefix);
	}
	
}
